<?php require_once("GetMime.php"); function get_data($url) {  	$ch = curl_init();  	$timeout = 5;  	curl_setopt($ch, CURLOPT_URL, $url);  	curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);  	curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);  	$data = curl_exec($ch);  	curl_close($ch);  	return $data;  } header("Content-disposition: attachment; ".$_GET['fname']); header("Content-type: ".get_mime($_GET['fname'])); echo get_data($_GET['path']); ?>